-- Creating address data
go
--Associate 1 random address to every user
while exists (select 1 from Users u where not exists (select 1 from UserAddress where Users_Id=u.id))
		select top 1 u.id as Users_Id, a.id as Addresses_Id
			from users u, addresses a
			where not exists (select 1 from UserAddress where Users_Id=u.id)
				and not exists (select 1 from UserAddress where Addresses_Id=a.id)
go
--Associate 1 random user to every address (means some users will have 2 addresses (a good thing)
while exists (select 1 from Addresses a where not exists (select 1 from UserAddress where Addresses_Id=a.id))
		select top 1 u.id as Users_Id, a.id as Addresses_Id
			from users u, addresses a
			where not exists (select 1 from UserAddress where Addresses_Id=a.id)
				and (select count(1) from UserAddress where Users_Id=u.id) = 1
go
